import styled from "styled-components";

export const GridItem = styled.article`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  box-shadow: 5px 5px 10px #999;
  width: 220px;

  div #hide {
    background-color: blue;
    display: flex;
    justify-content: center;
    width: 100%;
    height: 100%;
    z-index: 1;
    text-align: center;
  }

  .title {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 10px;

    span {
      font-size: 20px;
      font-weight: bold;
      margin-left: 10px;
    }

    svg {
      cursor: pointer;
    }
  }

  @media (max-width: 550px) {
    margin: 0 30%;
  }
`;
