import styled from "styled-components";

export const GridItem = styled.article`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  box-shadow: 5px 5px 10px #999;
  width: 220px;
  position: relative;

  section {
    display: flex;
    flex-direction: column;
    background-color: #f7f7f7;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: 1;

    h1 {
      font-size: 1.4rem;
      margin-left: 10px;
    }

    ul {
      list-style: none;
      margin-left: 10px;

      h1 {
        margin-top: 10px;
      }
    }

    li {
      font-size: 1rem;
      margin-top: 10px;
    }
  }

  .title {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 10px;
    z-index: 1;

    span {
      font-size: 20px;
      font-weight: bold;
      margin-left: 10px;
    }

    svg {
      cursor: pointer;
    }
  }

  @media (max-width: 550px) {
    margin: 0 30%;
  }
`;
