import styled from "styled-components";
import { Element } from "react-scroll";

export const Container = styled.div`
  overflow: auto;
  max-height: 88vh;
  margin-left: 20px;

  /* width */
  &::-webkit-scrollbar {
    width: 10px;
  }

  /* Track */
  &::-webkit-scrollbar-track {
    box-shadow: inset 0 0 5px grey;
    border-radius: 10px;
  }

  /* Handle */
  &::-webkit-scrollbar-thumb {
    background: #666;
    border-radius: 10px;
  }

  /* Handle on hover */
  &::-webkit-scrollbar-thumb:hover {
    background: #333;
  }

  div {
    display: inline-grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    grid-gap: 30px;
    max-width: 75vw;
    padding: 0 10px;

    @media (max-width: 550px) {
      margin: auto;
    }
  }

  @media (max-width: 550px) {
    margin-left: 0px;
  }
`;

export const GridItem = styled.article`
  padding: 20px;
  box-shadow: 5px 5px 10px #999;
  width: 200px;

  div {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    margin-top: 10px;

    span {
      font-size: 20px;
      font-weight: bold;
      margin-left: 10px;
    }
  }

  @media (max-width: 550px) {
    margin: 0 30%;
  }
`;

export const GridTitle = styled(Element)`
  font-size: 24px;
  font-weight: bold;
  margin-top: 50px;
  margin-bottom: 20px;
  margin-left: 20px;
  white-space: nowrap;

  @media (max-width: 550px) {
    margin-left: 0px;
    margin-top: 12px;
    margin-bottom: 5px;
  }
`;
