import React from "react";
import { IoIosArrowUp } from "react-icons/io";

import { GridItem } from "./styles";
import Image from "react-bootstrap/Image";

import pizzasDB from "./pizzasDB";

export default function GridPizzas() {
  function handleModalChange() {
    {
      pizzasDB.map((pizza) =>
        document.querySelector("section").classList.toggle("hide")
      );
    }
  }

  return (
    <>
      {pizzasDB.map((pizza) => (
        <>
          <GridItem>
            <section id={pizza.id} key={pizza.id} className="hide">
              <h1>{pizza.title}</h1>
              <ul>
                <h1>
                  INGREDIENTES <hr />
                </h1>
                {pizza.ingredientes.map((ingrediente) => (
                  <li>{ingrediente}</li>
                ))}
              </ul>
              <ul>
                <h1>
                  INFORMAÇÕES <hr />
                </h1>
                <p>{pizza.informacoes}</p>
              </ul>
            </section>
            <Image src={pizza.image} />
            <div className="title">
              <span>{pizza.title}</span>
              <IoIosArrowUp size={30} onClick={handleModalChange} />
            </div>
          </GridItem>
        </>
      ))}
    </>
  );
}
