import styled from "styled-components";
import { Element } from "react-scroll";

export const GridItem = styled.article`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  box-shadow: 5px 5px 10px #999;
  width: 220px;

  .title {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 10px;

    span {
      font-size: 20px;
      font-weight: bold;
      margin-left: 10px;
    }
  }

  @media (max-width: 550px) {
    margin: 0 30%;
  }
`;

export const GridTitle = styled(Element)`
  font-size: 24px;
  font-weight: bold;
  margin-top: 50px;
  margin-bottom: 20px;
  margin-left: 20px;
  white-space: nowrap;

  @media (max-width: 550px) {
    margin-left: 0px;
    margin-top: 12px;
    margin-bottom: 5px;
  }
`;
