import React from "react";
import { IoIosArrowUp } from "react-icons/io";

import { GridItem } from "./styles";
import Image from "react-bootstrap/Image";

import pizzasDB from "./pizzasDB";

export default function GridPizzas() {
  /*function handleModalChange() {
    document.querySelectorAll("section").classList.toggle("hide");
  }*/

  () => {
    const pizzas = pizzasDB.map((pizza) => ({ ...pizza, hide: false }));

    const getSectionClassName = (pizza) => (pizza.hide ? "hide" : undefined);

    const handleModalChange = (pizza) => {
      pizza.hide = !pizza.hide;
    };
  };

  return (
    <>
      {pizzasDB.map((pizza) => (
        <>
          <GridItem>
            <section className={getSectionClassName(pizza)}>
              <h1>{pizza.title}</h1>
              <ul>
                <h1>
                  INGREDIENTES <hr />
                </h1>
                {pizza.ingredientes.map((ingrediente) => (
                  <li>{ingrediente}</li>
                ))}
              </ul>
              <ul>
                <h1>
                  INFORMAÇÕES <hr />
                </h1>
                <p>{pizza.informacoes}</p>
              </ul>
            </section>
            <Image src={pizza.image} />
            <div className="title">
              <span>{pizza.title}</span>
              <IoIosArrowUp size={30} onClick={handleModalChange(pizza)} />
            </div>
          </GridItem>
        </>
      ))}
    </>
  );
}
